function Zeng_Data_Info(varargin)%Stripchart=get(gcf,'userdata')%get(gcbf,'tag')%not always%get(gcf,'tag') %Alway stripchartUD=get(0,'userdata');if strcmp('Stripchart',get(gcbf,'tag'));   FromStripchart=1;	Stripchart=get(gcf,'userdata');else   FromStripchart=0;   Stripchart=get(gcf,'userdata');endContinue=1;if  ~isempty(Stripchart.Data_Info)   figure(Stripchart.Data_Info.Figure);   if  FromStripchart 		Continue=0;   else		close;          endelseendif Continue   Data_Info=[];   Data_Info.Parent=Stripchart.Figure;   if FromStripchart;		Data_Info.Figure=figure(...  			'Units','pixels',...  			'Position',UD.Ref.Position(UD.HD.Data_Info.Figure,:));   else      Data_Info.Figure=dialog(...  			'Units','pixels',...        	'Position',UD.Ref.Position(UD.HD.Data_Info.Figure,:));  	end   set(Data_Info.Figure,...     	'Color',[0.8 0.8 0.8], ...		'MenuBar','none', ...  		'Name','Data Information',...	  	'NumberTitle','off',...  		'Position',UD.Ref.Position(UD.HD.Data_Info.Figure,:), ...	   'Tag','Data Information',...      'DeleteFcn',[....       	'Data_Info=get(gcbf,''userdata'');',...         'Stripchart=get(Data_Info.Parent,''userdata'');',...         'Stripchart.Data_Info=[];',...         'set(Stripchart.Figure,''userdata'',Stripchart);',...         'close(gcbf);']);   Stripchart.Data_Info.Figure=Data_Info.Figure;   set(Stripchart.Figure,'userdata',Stripchart);  	uicontrol('Parent',Data_Info.Figure, ...		'Units','normalized', ...		'BackgroundColor',[0.8 0.8 0.8], ...		'HorizontalAlignment','right', ...		'Position',[0.01 0.691318 0.12 0.08], ...		'String','Subject:', ...		'Style','text', ...		'Tag','StaticText1');	uicontrol('Parent',Data_Info.Figure, ...		'Units','normalized', ...		'BackgroundColor',[0.8 0.8 0.8], ...		'HorizontalAlignment','right', ...		'Position',[0.01 0.565916 0.12 0.08], ...		'String','Channels:', ...		'Style','text', ...		'Tag','StaticText1');	uicontrol('Parent',Data_Info.Figure, ...		'