Ver,'String',UD.Temp.Head.Sys_Ver)	   set(Data_Info.Text.Comment,'String',UD.Temp.Head.Comment)	end	if nargin == 1     % Normal Data information window   	uicontrol('Parent',Data_Info.Figure, ...	      'Units','normalized', ...	      'Position',[0.42266 0.0192926 0.163895 0.0836013], ...	      'String','OK', ...	      'Tag','OK',...         'callback',[...            'Data_Info=get(gcbf,''userdata'');',...            'Stripchart=get(Data_Info.Parent,''userdata'');',...            'Stripchart.Data_Info=[];',...            'set(Stripchart.Figure,''userdata'',Stripchart);',...            'close(gcbf);']);		   %'UD=get(0,''userdata'');',...			%'Comment=get(Data_Info.Text.Comment,''String'');',...			%'if strcmp(Comment,UD.Head.Comment);',...			%'figure(10);',...			%'   Zeng(''Error'',''Are you sure to change the comment ???'');',...			%'else;',...			%' figure(20);',...			%'   close(Data_Info.Figure);',...      	%'end']);	elseif nargin == 2 %   ""    add No head file windows.		uicontrol('Parent',Data_Info.Figure, ...			'Units','normalized', ...			'FontUnits','normalized', ...			'BackgroundColor',[0.8 0.8 0.8], ...			'FontSize',0.213333, ...			'HorizontalAlignment','left', ...			'Position',[0.647482 0.748428 0.206235 0.13522], ...			'String','These data came from', ...			'Style','text', ...			'Tag','StaticText1');		uicontrol('Parent',Data_Info.Figure, ...			'Units','normalized', ...			'FontUnits','normalized', ...			'BackgroundColor',[0.8 0.8 0.8], ...			'FontSize',0.102564, ...			'HorizontalAlignment','left', ...			'Position',[0.640288 0.569182 0.227818 0.333333], ...			'Style','frame', ...			'Tag','Frame1');			Data_Info.OP.PC = uicontrol('Parent',Data_Info.Figure, ...	      'Units','normalized', ...	      'BackgroundColor',[0.8 0.8 0.8], ...	      'Position',[0.676259 0.688679 0.136691 0.081761], ...	      'String','PC', ...	      'Style','radiobutton', ...         'callback',[...            'Data_Info=get(gcbf,''userd