ead.Chans);',...	         '   if length(UD.Temp.Data) ~=UD.Temp.Head.Samples*UD.Temp.Head.Chans;',...	         '        Zeng(''Error'',''Samples and Channels do not match with raw data size'');',...	         '   else;',...	         '     Zeng(''Stripchart'',''Initial'');',...            '     Stripchart=get(gcf,''userdata'');',...             '     Stripchart.Data=UD.Temp.Data;',...            '     Stripchart.Head=UD.Temp.Head;',...	         'disp(Stripchart.Head);',...            '     if get(Data_Info.OP.PC,''value'') ==1;',...	         '        Stripchart.Data=reshape(Stripchart.Data,Stripchart.Head.Samples,Stripchart.Head.Chans);',...	         '     else;',...	         '        Stripchart.Data=reshape(Stripchart.Data,Stripchart.Head.Chans,Stripchart.Head.Samples)'';',...	         '     end;',...	         '     Stripchart.Current_ch=1;',...	         '     Stripchart.Time1=1;',...            '     Stripchart.Time2=Stripchart.Head.Samples;',...	         '     set(Stripchart.Edit.Channel,''string'',Stripchart.Current_ch);',...	         '     set(Stripchart.Edit.Time1,''string'',Stripchart.Time1);',...	         '     set(Stripchart.Edit.Time2,''string'',Stripchart.Time2);',...	         '     axes(Stripchart.Axes);plot(Stripchart.Data(:,Stripchart.Current_ch));',...	         '     set(Stripchart.Figure,''name'',[''File name: '' Stripchart.Head.FileName]);',...	         '     set(Stripchart.Figure,''userdata'',Stripchart);',...	         '     dlmwrite([Stripchart.Head.Path Stripchart.Head.FileName ''.h''],'''');',...	         '     FID=fopen([Stripchart.Head.Path Stripchart.Head.FileName ''.h''],''w'');',...   	      '     fprintf(FID,''=================================\n'');',...	         '     fprintf(FID,''CWRU Cardio Mapping System header\n'');',...   	      '     fprintf(FID,''=================================\n'');',...	         '     fprintf(FID,[''subject\t'' Stripchart.Head.Subject ''\n'']);',...	         '     fprintf(FID,[''date\t'' Stripchart.Head.Date ''\n'']);',...	         '     fprintf(FID,[''gain\t'' num2str(Stripchart.Head.Gain) ''\n'']);',...	         '     fprintf(FID,[''srate\t'' num2str(Stripchart.Head.SRate) ''\n'']);',...	         '     fprintf(FID,[''samples\t'' num2str(Stripchart.Head.Samples) ''\n'']);',...	         '     fprintf(FID,[''chans\t'' num2str(Stripchart.Head.Chans) ''\n'']);',...	         '     fprintf(FID,[''comment\t'' Stripchart.Head.Comment ''\n'']);',...	         '     fprintf(FID,[''lut\t'' Stripchart.Head.LUT ''\n'']);',...	         '     fprintf(FID,[''SysVer\t'' Stripchart.Head.Sys_Ver ''\n'']);',...	         '     Status=fclose(FID);',...	         '     UD.Temp.Head=[];',...	         '     UD.Temp.Data=[];',...            '     set(0,''userdata'',UD);',...	         '     if Status== -1;',... % The head file was not able to save.	         '         Zeng(''Error'',''I Could not save head file for you'');',...            '     end;',...            '     close(Data_Info.Figure);',...	         '   end;',...% if length(UD.Data) ~=UD.Head.Samples*UD.Head.Chans;',...	         'else;',...	         '   Zeng(''Error'',''Samples and Channels do not match with raw data size'');',... %'   Zeng(''Error'',''Do not be Stupid to add letters in "Samples and Channels" parts'');',...	         'end;']); %   'if length(UD.Head.Samples)|length(UD.Head.Chans);',...			   uicontrol('Parent',Data_Info.Figure, ...		      'Units','normalized', ...	      	'Position',[0.56057 0.0257235 0.16152 0.0803859], ...		      'String','Cancel', ...	   	   'Tag','Cancel',...	      	'callback',[...	            'Data_Info=get(gcbf,''userdata'');',...   	         'Stripchart=get(Data_Info.Parent,''userdata'');',...      	      'Stripchart.Data_Info=[];',...         	   'set(Stripchart.Figure,''userdata'',Stripchart);',...	            'UD=get(0,''userdata'');',...		         'UD.Temp.Head=[];',...%'set(Stripchart.Text.FileName,''string'','' '');',...		         'close(gcbf);']);	end %	if nargin == 1     % Normal Data information window	set(Data_Info.Figure,'userdata',Data_Info);end %if  ~isempty(Stripchart.Data_Info.Figure)